import React, { useState, useEffect } from 'react';

function AdminDashboard() {
  const [dashboardData, setDashboardData] = useState({
    totalOrders: 0,
    totalRevenue: 0,
    totalProducts: 0,
    totalUsers: 0
  });

  useEffect(() => {
    // Simulasi pengambilan data dari API
    // Dalam implementasi nyata, Anda akan mengambil data dari backend
    setTimeout(() => {
      setDashboardData({
        totalOrders: 150,
        totalRevenue: 15000000,
        totalProducts: 75,
        totalUsers: 250
      });
    }, 1000);
  }, []);

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>

      <div className="dashboard-stats">
        <div className="stat-card">
          <h3>Total Pesanan</h3>
          <p>{dashboardData.totalOrders}</p>
        </div>

        <div className="stat-card">
          <h3>Total Pendapatan</h3>
          <p>Rp {dashboardData.totalRevenue.toLocaleString()}</p>
        </div>

        <div className="stat-card">
          <h3>Total Produk</h3>
          <p>{dashboardData.totalProducts}</p>
        </div>

        <div className="stat-card">
          <h3>Total Pengguna</h3>
          <p>{dashboardData.totalUsers}</p>
        </div>
      </div>

      <div className="dashboard-actions">
        <button className="action-button">Kelola Produk</button>
        <button className="action-button">Lihat Pesanan</button>
        <button className="action-button">Kelola Pengguna</button>
      </div>

      <style jsx>{`
        .admin-dashboard {
          padding: 20px;
          max-width: 1200px;
          margin: 0 auto;
        }

        h1 {
          color: #333;
          margin-bottom: 30px;
          text-align: center;
        }

        .dashboard-stats {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
          gap: 20px;
          margin-bottom: 30px;
        }

        .stat-card {
          background-color: #fff;
          border-radius: 8px;
          padding: 20px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
          text-align: center;
        }

        .stat-card h3 {
          margin-top: 0;
          color: #666;
          font-size: 16px;
        }

        .stat-card p {
          margin-bottom: 0;
          font-size: 24px;
          font-weight: bold;
          color: #333;
        }

        .dashboard-actions {
          display: flex;
          justify-content: center;
          gap: 15px;
          flex-wrap: wrap;
        }

        .action-button {
          background-color: #4a90e2;
          color: white;
          border: none;
          padding: 12px 20px;
          border-radius: 4px;
          cursor: pointer;
          font-size: 16px;
          transition: background-color 0.3s;
        }

        .action-button:hover {
          background-color: #357abD;
        }
      `}</style>
    </div>
  );
}

export default AdminDashboard;
