import React from 'react';

const AdminDashboard = () => {
  return (
    <div className="admin-dashboard-wrapper" style={{ padding: 24 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
          <h1 style={{ fontSize: '28px', margin: 0, fontWeight: 'normal' }}>Dashboard</h1>
          <p style={{ margin: '5px 0 0', color: '#666' }}>Welcome, Admin!</p>
        </div>
        {/* Tombol Website menuju port pengguna */}
        <a
          href="http://localhost:3012/"
          className="website-btn"
          style={{
            backgroundColor: '#e52b2b',
            color: 'white',
            padding: '10px 25px',
            borderRadius: '5px',
            textDecoration: 'none',
            fontWeight: 'bold'
          }}
        >
          Website
        </a>
      </div>

      <div style={{ marginTop: 24 }}>
        <div style={{
          backgroundColor: '#e52b2b',
          borderRadius: '10px',
          padding: '30px',
          color: 'white'
        }}>
          <h2 style={{ fontSize: '24px', margin: 0, fontWeight: 'normal' }}>Katalog Produk</h2>
          <p style={{ marginTop: 8 }}>Kelola produk Anda dari sini.</p>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
