import React, { useState, useEffect } from 'react';
import '../App.css';

function AdminDashboard() {
  const [dashboardData, setDashboardData] = useState({
    totalOrders: 0,
    totalRevenue: 0,
    totalProducts: 0,
    totalUsers: 0
  });

  useEffect(() => {
    // Simulasi data dashboard
    setDashboardData({
      totalOrders: 156,
      totalRevenue: 12500,
      totalProducts: 48,
      totalUsers: 230
    });
  }, []);

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Admin Dashboard</h1>
        <div className="user-info">
          <span>Admin</span>
          <button className="logout-btn">Logout</button>
        </div>
      </header>

      <div className="dashboard-stats">
        <div className="stat-card">
          <h3>Total Orders</h3>
          <p className="stat-value">{dashboardData.totalOrders}</p>
        </div>
        <div className="stat-card">
          <h3>Total Revenue</h3>
          <p className="stat-value">${dashboardData.totalRevenue}</p>
        </div>
        <div className="stat-card">
          <h3>Total Products</h3>
          <p className="stat-value">{dashboardData.totalProducts}</p>
        </div>
        <div className="stat-card">
          <h3>Total Users</h3>
          <p className="stat-value">{dashboardData.totalUsers}</p>
        </div>
      </div>

      <div className="dashboard-content">
        <h2>Recent Activity</h2>
        <div className="activity-list">
          <p>No recent activities to display.</p>
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
